        <div className="dot-progress">
          {times(5, (i) => {
            const filled = language.level >= i + 1;
            return (
              <span
                className={`dot ${filled && hasLoaded ? " filled" : ""}`}
                style={{ animationDelay: `${i / 4}s` }}>
              </span>
            );
          })}
        </div>
      </li>
    );
}

export default Language;
